<content>
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Arial', sans-serif;
    background-color: #f0f8ff;
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    padding: 20px;
}

.game-container {
    width: 100%;
    max-width: 800px;
    background-color: #fff;
    border-radius: 10px;
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
    overflow: hidden;
}

.game-header {
    background-color: #4a90e2;
    color: white;
    padding: 15px 20px;
    text-align: center;
}

.game-header h1 {
    font-size: 24px;
    margin-bottom: 10px;
}

.score-container {
    display: flex;
    justify-content: space-around;
    font-size: 18px;
    font-weight: bold;
}

.game-area {
    position: relative;
    background-color: #e9f5ff;
    aspect-ratio: 4/3;
    width: 100%;
    overflow: hidden;
}

#gameCanvas {
    display: block;
    width: 100%;
    height: 100%;
}

.screen {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.7);
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    text-align: center;
    padding: 20px;
}

.hidden {
    display: none;
}

.screen h2 {
    font-size: 28px;
    margin-bottom: 20px;
}

.screen p {
    font-size: 18px;
    margin-bottom: 30px;
    max-width: 80%;
}

button {
    background-color: #4a90e2;
    color: white;
    border: none;
    padding: 12px 24px;
    font-size: 18px;
    border-radius: 5px;
    cursor: pointer;
    transition: background-color 0.3s;
}

button:hover {
    background-color: #357ae8;
}

.game-controls {
    background-color: #4a90e2;
    padding: 15px;
}

.mobile-controls {
    display: flex;
    justify-content: space-between;
    margin-bottom: 10px;
}

.mobile-controls button {
    width: 48%;
    font-size: 24px;
}

.game-info {
    color: white;
    text-align: center;
    font-size: 14px;
}

/* Mobile responsiveness */
@media (min-width: 768px) {
    .mobile-controls {
        display: none;
    }
}

@media (max-width: 767px) {
    .game-header h1 {
        font-size: 20px;
    }

    .score-container {
        font-size: 16px;
    }

    .screen h2 {
        font-size: 22px;
    }

    .screen p {
        font-size: 16px;
    }

    button {
        padding: 10px 20px;
        font-size: 16px;
    }
}
</content>
